(1312, 'http://www.samsung.com/iran/apps/smartthings/', 'http://image.samsung.com/apps/star/smartthings/common/search/apps_smartthings_search_logo.png', 'smartthings logo', '', 0, 0),
(1313, 'http://www.samsung.com/iran/apps/smartthings/', 'http://images.samsung.com/is/content/samsung/p5/iran/apps/smartthings/icons/200821-03-apps-and-service-smartthings-voiceassistant-section-00-5-pc.svg', ' ', '', 0, 0),
